import {TAREAS_PROYECTO
} from '../../types/index'

export default (state,action)=>{
    switch(action){
        case FORMULARIO_PROYECTO:
            return ({
                ...state,
                formulario:true,
                error:false
            })
        default: return state;
    }
}
