import {TAREAS_PROYECTO
} from '../../types/index'

export default (state,action)=>{
    switch(action.type){
        case TAREAS_PROYECTO:
            return ({
                ...state,
                tareasProyecto:state.tareas.filter(tarea=>tarea.idProyecto===action.payload)
            })
        case CREAR_TAREA:
            return ({
                ...state,
                tareas: [...state.tareas, action.payload]
            })
        default: return state;
    }
}
