import {TAREAS_PROYECTO,
    CREAR_TAREA,
    VALIDAR_TAREA,
    ELIMINAR_TAREA
} from '../../types/index'

export default (state,action)=>{
    switch(action.type){
        case TAREAS_PROYECTO:
            return ({
                ...state,
                tareasProyecto:state.tareas.filter(tarea=>tarea.idProyecto===action.payload)
            })
        case CREAR_TAREA:
            return ({
                ...state,
                tareas: [...state.tareas, action.payload],
                errorTarea:false
            })
        case VALIDAR_TAREA:
            return ({
                ...state,
                errorTarea:true
            })
        default: return state;
    }
}
