    const {usuarioAutenticado, usuario} = aContext

    useEffect(() => {
        usuarioAutenticado();
    }, [])

    return ( 
        <header className="app-header">
            {usuario ? <p className="nombre-usuario">Hola <span>{usuario.nombre}</span></p> : null}

            <nav className="nav-principal">
