import {TAREAS_PROYECTO
} from '../../types/index'

export default (state,action)=>{
    switch(action.type){
        case TAREAS_PROYECTO:
            return ({
                ...state,
                tareas:state.tareas.filter(tarea=>tarea.idProyecto===action.payload)
            })
        default: return state;
    }
}
