import React, {Fragment, useContext} from 'react';
import proyectoContext from '../../context/Proyecto/proyectoContext' 
import Tarea from './Tarea'

const ListadoTareas = () => {
    const tareasProyecto = [
        {nombre:'Elegir plataforma', estado:true},
        {nombre:'Elegir lenguaje', estado:false},
        {nombre:'Elegir hosting', estado:false},
        {nombre:'Desarrollar', estado:true}
    ]

    const pContext = useContext(proyectoContext)

    const {formulario, mostrarFormulario, error, agregarProyecto, mostrarError} = pContext



    return ( 
        <Fragment>
            <h2>Tareas del proyecto: Tienda Virtual</h2>
            <ul>
                {(tareasProyecto.lenght===0)?
                    (<li><p>No hay tareas asignadas al proyecto</p></li>)
                :
                    (tareasProyecto.map(
                        tarea=> (<Tarea
                                    tarea= {tarea}
                                    />
                                )
                    ))
                }
            </ul>
            <button     
                type="button"
                className="btn btn-eliminar"
            >Eliminar Proyecto &times;</button>
        </Fragment>
     );
}

export default ListadoTareas;
