import React, {useReducer} from 'react';

import uuid from 'uuid'
import proyectoContext from './proyectoContext'
import proyectoReducer from './proyectoReducer'
import {
    FORMULARIO_PROYECTO, 
    OBTENER_PROYECTOS,
    AGREGAR_PROYECTOS,
    VALIDAR_FORMULARIO,
    SELECCIONAR_PROYECTO,
    ELIMINAR_PROYECTO} from '../../types/index'



const ProyectoState = props=>{

    const proyectostemp = [
        {id: 1, nombre: "tienda virtual"},
        {id: 2, nombre: "recetas"},
        {id: 3, nombre: "Armar Racing 2021"},
        {id: 4, nombre: "Diseño sitio web"}
    ]

    const initialState={
        proyectos : [],
        formulario:false,
        error: false, 
        proyecto: null
    }

    //Dispatch para ejecutar las acciones
    const [state,dispatch] = useReducer(proyectoReducer, initialState)


    //defino las funciones para el CRUD de formularios
    const mostrarFormulario =()=>{
        dispatch({
            type: FORMULARIO_PROYECTO
        })
    }

    const obtenerProyectos = ()=>{
        dispatch({
            type: OBTENER_PROYECTOS,
            payload: proyectostemp
        })
    }

    const agregarProyecto = proyecto =>{
        proyecto.id = uuid.v4();
        dispatch({
            type: AGREGAR_PROYECTOS,
            payload: proyecto
        })
    }

    const mostrarError = () =>{
        dispatch({
            type: VALIDAR_FORMULARIO
        })
    }

    const seleccionarProyecto = (idProyecto) =>{
        dispatch({
            type: SELECCIONAR_PROYECTO,
            payload:idProyecto
        })
    }

    const eliminarProyecto = (idProyecto) =>{
        dispatch({
            type:ELIMINAR_PROYECTO,
            payload:idProyecto
        })
    }

    return (
        <proyectoContext.Provider
            value={{
                proyectos: state.proyectos,
                formulario: state.formulario,
                error: state.error,
                proyecto: state.proyecto,
                mostrarFormulario, 
                obtenerProyectos,
                agregarProyecto,
                mostrarError, 
                seleccionarProyecto,
                eliminarProyecto
            }}
        >
            {props.children}
        </proyectoContext.Provider>
    )

}

export default ProyectoState;
