import React, {useReducer} from 'react';

import tareaReducer from './tareaReducer'
import TareaContext from './tareaContext';

import {TAREAS_PROYECTO,
    CREAR_TAREA
} from '../../types/index'


const TareaState = props => {

    const initialState={
        tareas : [
            {nombre:'Elegir plataforma', estado:true, idProyecto:1},
            {nombre:'Elegir lenguaje', estado:false, idProyecto:2},
            {nombre:'Elegir hosting', estado:false, idProyecto:3},
            {nombre:'Desarrollar', estado:true, idProyecto:4},
            {nombre:'Elegir plataforma', estado:true, idProyecto:3},
            {nombre:'Elegir lenguaje', estado:false, idProyecto:2},
            {nombre:'Elegir hosting', estado:false, idProyecto:1},
            {nombre:'Elegir plataforma', estado:true, idProyecto:4},
            {nombre:'Elegir lenguaje', estado:false, idProyecto:3},
            {nombre:'Elegir hosting', estado:false, idProyecto:2}
        ], 
        tareasProyecto : [],
        errorTarea:false
    }
     //Dispatch para ejecutar las acciones
    const [state,dispatch] = useReducer(tareaReducer, initialState)

    const obtenerTareasProyecto = (proyectoID) =>{
        dispatch({
            type:TAREAS_PROYECTO,
            payload:proyectoID
        })
    }

    const crearTarea = tarea =>{
        dispatch({
            type:CREAR_TAREA,
            payload:tarea
        })
    }

     return (
         <TareaContext.Provider
            value={{
                tareas:state.tareas,
                tareasProyecto: state.tareasProyecto,
                obtenerTareasProyecto,
                crearTarea
            }}
         >
             {props.children}
         </TareaContext.Provider>
     )


}

export default TareaState
